extension CategoriesMock : CategoriesRemoteDataSourceProtocol{
    func fetchCategoryProducts(collection_id: String, compilitionHandler: @escaping (Shoppinist.ProductModel?) -> Void) {
        let data = Data (CategoriesMock.categoryData.utf8)
        do{
            let result = try JSONDecoder().decode(ProductModel.self, from: data)
            compilitionHandler(result)
        }catch let error{
            print(error.localizedDescription)
            compilitionHandler(nil)
        }
    }
    
    
